SELECT AVG("transactions_1k"."ProductID") as agg_0, AVG("yearmonth"."Consumption") as agg_1, AVG("yearmonth"."Date" + "transactions_1k"."CustomerID") as agg_2 FROM "customers" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "gasstations"."Segment" IN ('Premium', 'Other', 'Value for money') AND ("yearmonth"."CustomerID" >= 752679 OR "yearmonth"."Date" != 201207) AND "transactions_1k"."Date" IN ('2012-08-23', '2012-08-24') AND "gasstations"."Country" != 'SVK' AND ("gasstations"."Country" = 'SVK' OR "gasstations"."Country" = 'SVK')