SELECT SUM("transactions_1k"."TransactionID" + "gasstations"."GasStationID") as agg_0, AVG("gasstations"."ChainID" + "transactions_1k"."ProductID") as agg_1, AVG("customers"."CustomerID" + "products"."ProductID") as agg_2 FROM "transactions_1k" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE "gasstations"."Segment" != 'Value for money' AND "customers"."Segment" NOT LIKE '%KA%M%'