SELECT COUNT(*) as agg_0, MAX("gasstations"."GasStationID" + "transactions_1k"."ProductID") as agg_1, AVG("yearmonth"."Date") as agg_2 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "customers"."Currency" NOT LIKE '%CZK%' AND "transactions_1k"."Date" IN ('2012-08-23', '2012-08-26') AND "transactions_1k"."ProductID" <= 2 AND "customers"."Segment" IN ('LAM') AND "products"."Description" NOT LIKE '%D%EU%'