SELECT MIN("transactions_1k"."GasStationID") as agg_0, MAX("transactions_1k"."CustomerID") as agg_1, AVG("transactions_1k"."Price") as agg_2 FROM "customers" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID"  WHERE "customers"."Currency" LIKE '%C%ZK%' AND "transactions_1k"."Time" = '13:25:00' AND "transactions_1k"."Price" <= 598.6760101701973 AND "transactions_1k"."CustomerID" >= 475509