SELECT MAX("transactions_1k"."CustomerID" + "gasstations"."ChainID") as agg_0, MAX("gasstations"."GasStationID" + "transactions_1k"."Price") as agg_1 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE "gasstations"."Country" != 'SVK' AND "customers"."Segment" LIKE '%SME%' AND "transactions_1k"."Date" = '2012-08-24' AND "gasstations"."Segment" = 'Value for money' AND "customers"."Currency" NOT LIKE '%EUR%' AND ("customers"."Currency" IN ('EUR') OR "customers"."Currency" = 'CZK')