SELECT AVG("products"."ProductID" + "transactions_1k"."TransactionID") as agg_0 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "products"."Description" NOT LIKE '%Natur%al%' AND "products"."ProductID" <= 102 AND "products"."Description" NOT LIKE '%(VAT-free)%' AND "gasstations"."Segment" != 'Noname' AND "gasstations"."Country" LIKE '%CZE%' AND "transactions_1k"."Date" != '2012-08-24'