SELECT COUNT(*) as agg_0, AVG("transactions_1k"."Price") as agg_1, AVG("gasstations"."ChainID") as agg_2 FROM "transactions_1k" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "products"."Description" IN ('Potraviny', 'Vratné obaly', 'Service charge', 'Ostatné sl.', 'Servisní poplatek', 'Umývanie voz') AND "transactions_1k"."Date" LIKE '%2012-08-24%' AND "transactions_1k"."Date" LIKE '%2012-08-%26%'