SELECT MIN("yearmonth"."CustomerID") as agg_0, MIN("transactions_1k"."ProductID" + "customers"."CustomerID") as agg_1, COUNT(*) as agg_2 FROM "customers" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "yearmonth"."CustomerID" <= 1128319 AND "transactions_1k"."Date" IN ('2012-08-23', '2012-08-24', '2012-08-25')