SELECT MAX("transactions_1k"."Amount") as agg_0, AVG("products"."ProductID") as agg_1, MIN("yearmonth"."Date" + "yearmonth"."Consumption") as agg_2 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "yearmonth"."CustomerID" BETWEEN 1181156 AND 1312392