SELECT AVG("customers"."CustomerID" + "transactions_1k"."Amount") as agg_0, MAX("transactions_1k"."GasStationID" + "yearmonth"."CustomerID") as agg_1, MIN("products"."ProductID" + "yearmonth"."CustomerID") as agg_2 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE ("transactions_1k"."Price" >= 628.807913496963 OR ("transactions_1k"."Price" >= 199.64522622274336 AND "transactions_1k"."Price" BETWEEN 67.86668088562719 AND 146.5255581665969)) AND "products"."Description" LIKE '%free)%' AND "gasstations"."ChainID" <= 34 AND "products"."Description" != 'Toll SVK toll services' AND "gasstations"."Country" NOT LIKE '%SVK%'