SELECT AVG("transactions_1k"."TransactionID") as agg_0, COUNT(*) as agg_1, AVG("transactions_1k"."GasStationID") as agg_2 FROM "customers" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "customers"."Currency" != 'CZK' AND "yearmonth"."CustomerID" BETWEEN 1070430 AND 1377420 AND "products"."Description" LIKE '%T%oll%' AND "customers"."Currency" NOT LIKE '%EUR%' AND "products"."Description" IN ('Katalyzátor', 'Provozní poplatek', 'Tisk reportu')