SELECT AVG("transactions_1k"."CustomerID" + "transactions_1k"."Amount") as agg_0, COUNT(*) as agg_1 FROM "yearmonth" LEFT OUTER JOIN "customers" ON "yearmonth"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "customers"."Currency" = 'CZK' AND "customers"."Segment" NOT LIKE '%KAM%' AND "gasstations"."GasStationID" >= 5397 AND ("customers"."Currency" != 'CZK' OR "customers"."Currency" IN ('EUR')) AND "gasstations"."Country" != 'CZE'