SELECT SUM("transactions_1k"."CardID" + "transactions_1k"."Price") as agg_0 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE ("customers"."Segment" = 'LAM' OR "customers"."Segment" LIKE '%SME%' OR "customers"."Segment" = 'KAM') AND "gasstations"."Country" != 'CZE' AND "gasstations"."Segment" IN ('Noname', 'Premium', 'Other') AND "transactions_1k"."Date" = '2012-08-25' AND "products"."Description" LIKE '%PL%'