SELECT SUM("transactions_1k"."CardID") as agg_0, MAX("transactions_1k"."Amount" + "transactions_1k"."TransactionID") as agg_1 FROM "products" LEFT OUTER JOIN "transactions_1k" ON "products"."ProductID" = "transactions_1k"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "products"."Description" IN ('Um√Ωvanie voz', 'Tisk reportu', 'Ostatni zbozi') AND "customers"."Currency" != 'CZK' AND "products"."ProductID" <= 608 AND "transactions_1k"."CardID" BETWEEN 370935 AND 420418