SELECT MAX("gasstations"."ChainID" + "transactions_1k"."GasStationID") as agg_0 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "gasstations"."Country" IN ('CZE') AND "gasstations"."Segment" != 'Other' AND "transactions_1k"."Date" LIKE '%2012-08-26%'