SELECT MAX("yearmonth"."Consumption") as agg_0 FROM "customers" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID"  WHERE "customers"."Segment" IN ('LAM') AND "yearmonth"."Date" BETWEEN 201307 AND 201310 AND "customers"."Currency" LIKE '%CZK%' AND "yearmonth"."Consumption" >= 737.3972826506115 AND ("customers"."Segment" LIKE '%SM%E%' OR "customers"."CustomerID" >= 1481916 OR "customers"."Segment" = 'LAM')