SELECT SUM("transactions_1k"."CardID") as agg_0, SUM("transactions_1k"."Price") as agg_1, COUNT(*) as agg_2 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE "gasstations"."Country" NOT LIKE '%C%ZE%' AND "gasstations"."GasStationID" <= 3045 AND "transactions_1k"."CardID" >= 695066