SELECT SUM("transactions_1k"."GasStationID") as agg_0, SUM("gasstations"."GasStationID") as agg_1 FROM "yearmonth" LEFT OUTER JOIN "customers" ON "yearmonth"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "gasstations"."Country" NOT LIKE '%SVK%' AND "customers"."Currency" IN ('EUR') AND "gasstations"."Segment" LIKE '%f%or%' AND "customers"."CustomerID" >= 1265475 AND "transactions_1k"."Date" NOT LIKE '%2012-08-24%' AND "transactions_1k"."Price" BETWEEN 224.73587974081312 AND 721.8072089219451