SELECT MIN("transactions_1k"."ProductID") as agg_0, MAX("products"."ProductID" + "transactions_1k"."Price") as agg_1 FROM "transactions_1k" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID"  WHERE "transactions_1k"."Date" = '2012-08-24' AND "products"."ProductID" <= 206