SELECT SUM("yearmonth"."CustomerID") as agg_0, MAX("yearmonth"."Date") as agg_1 FROM "yearmonth" LEFT OUTER JOIN "customers" ON "yearmonth"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE ("transactions_1k"."CardID" >= 548820 OR "transactions_1k"."CardID" >= 712044) AND "customers"."Currency" IN ('EUR') AND "gasstations"."Segment" IN ('Discount', 'Noname', 'Value for money') AND "transactions_1k"."Date" IN ('2012-08-26', '2012-08-24', '2012-08-25') AND "gasstations"."Country" NOT LIKE '%SVK%'