SELECT AVG("yearmonth"."Consumption" + "customers"."CustomerID") as agg_0, AVG("transactions_1k"."CardID" + "customers"."CustomerID") as agg_1 FROM "transactions_1k" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID"  WHERE "customers"."Currency" IN ('CZK') AND "products"."Description" = 'Garage commission' AND ("transactions_1k"."Date" LIKE '%2012-08-26%' OR "transactions_1k"."Date" NOT LIKE '%2012-08-24%' OR "transactions_1k"."Date" != '2012-08-24')