SELECT AVG("transactions_1k"."GasStationID") as agg_0 FROM "customers" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID" LEFT OUTER JOIN "gasstations" ON "transactions_1k"."GasStationID" = "gasstations"."GasStationID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "transactions_1k"."Time" != '03:44:00' AND "customers"."CustomerID" <= 1572176