SELECT MIN("transactions_1k"."CardID" + "transactions_1k"."CustomerID") as agg_0, MAX("transactions_1k"."CustomerID" + "transactions_1k"."GasStationID") as agg_1, MIN("transactions_1k"."ProductID") as agg_2 FROM "customers" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "transactions_1k" ON "customers"."CustomerID" = "transactions_1k"."CustomerID"  WHERE "yearmonth"."Date" = 201208 AND ("transactions_1k"."Date" NOT LIKE '%2012-08-24%' OR "transactions_1k"."TransactionID" >= 66) AND "customers"."Segment" != 'SME'