SELECT MIN("customers"."CustomerID") as agg_0, SUM("transactions_1k"."TransactionID") as agg_1, AVG("transactions_1k"."CustomerID" + "transactions_1k"."ProductID") as agg_2 FROM "gasstations" LEFT OUTER JOIN "transactions_1k" ON "gasstations"."GasStationID" = "transactions_1k"."GasStationID" LEFT OUTER JOIN "customers" ON "transactions_1k"."CustomerID" = "customers"."CustomerID" LEFT OUTER JOIN "yearmonth" ON "customers"."CustomerID" = "yearmonth"."CustomerID" LEFT OUTER JOIN "products" ON "transactions_1k"."ProductID" = "products"."ProductID"  WHERE "yearmonth"."Consumption" <= 2313.517212094028 AND "yearmonth"."Date" BETWEEN 201170 AND 201307