SELECT MIN("HOUSEHOLDS"."INCOME_RANK_4" + "HOUSEHOLDS"."HOUSEHOLD_ID") as agg_0, SUM("HOUSEHOLDS"."AGE_REF") as agg_1 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."HOUSEHOLD_ID" BETWEEN 4012229 AND 6023315 AND "EXPENDITURES"."YEAR" = 2017 AND "HOUSEHOLDS"."INCOME_RANK_2" BETWEEN 0.4958430633596712 AND 0.692448702978252