SELECT MAX("HOUSEHOLD_MEMBERS"."AGE" + "HOUSEHOLDS"."INCOME_RANK_5") as agg_0, SUM("EXPENDITURES"."COST") as agg_1 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."GIFT" BETWEEN 0 AND 1 AND "HOUSEHOLD_MEMBERS"."YEAR" BETWEEN 2015 AND 2018 AND ("HOUSEHOLDS"."INCOME_RANK_5" BETWEEN 0.5989425667305542 AND 0.9106723281781557 OR "HOUSEHOLDS"."INCOME_RANK_2" >= 0.8577234265779716) AND ("HOUSEHOLDS"."INCOME_RANK_3" BETWEEN 0.4390840174585402 AND 0.8238232425862066 OR ("HOUSEHOLDS"."YEAR" = 2015 AND "HOUSEHOLDS"."INCOME_RANK_3" <= 0.677041415575036)) AND "HOUSEHOLDS"."INCOME_RANK_4" >= 0.7788744022946559