SELECT SUM("HOUSEHOLDS"."AGE_REF") as agg_0, AVG("HOUSEHOLDS"."YEAR") as agg_1, MAX("HOUSEHOLDS"."INCOME_RANK" + "HOUSEHOLD_MEMBERS"."WORK_STATUS") as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."AGE" = 44