SELECT SUM("HOUSEHOLDS"."HOUSEHOLD_ID") as agg_0, MAX("HOUSEHOLDS"."INCOME_RANK_MEAN" + "HOUSEHOLD_MEMBERS"."YEAR") as agg_1, MAX("HOUSEHOLDS"."INCOME_RANK_1") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_MEAN" <= 0.5778386468801686 AND "HOUSEHOLD_MEMBERS"."YEAR" BETWEEN 2016 AND 2019 AND "HOUSEHOLDS"."INCOME_RANK_1" <= 0.8157501388767756 AND "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" <= 4181156 AND ("HOUSEHOLD_MEMBERS"."MARITAL" <= 1 OR "HOUSEHOLD_MEMBERS"."MARITAL" = 5 OR "HOUSEHOLD_MEMBERS"."MARITAL" = 2) AND "HOUSEHOLDS"."INCOME_RANK_3" >= 0.017046142473299997