SELECT AVG("EXPENDITURES"."COST" + "EXPENDITURES"."HOUSEHOLD_ID") as agg_0, SUM("HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" + "HOUSEHOLDS"."INCOME_RANK_MEAN") as agg_1, MIN("EXPENDITURES"."EXPENDITURE_ID") as agg_2 FROM "HOUSEHOLDS" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."COST" <= 3.344919163666842 AND "HOUSEHOLD_MEMBERS"."SEX" >= 2