SELECT SUM("HOUSEHOLD_MEMBERS"."YEAR") as agg_0, AVG("EXPENDITURES"."PRODUCT_CODE") as agg_1 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."SEX" != 1 AND "HOUSEHOLDS"."INCOME_RANK_MEAN" >= 0.06545579716794521 AND "HOUSEHOLDS"."INCOME_RANK_5" BETWEEN 0.2100971591212247 AND 0.5501719959738428 AND "EXPENDITURES"."IS_TRAINING" = 1 AND "EXPENDITURES"."EXPENDITURE_ID" >= 1117460 AND "EXPENDITURES"."COST" >= 5.043707566465683