SELECT AVG("HOUSEHOLD_MEMBERS"."SEX") as agg_0, AVG("HOUSEHOLD_MEMBERS"."AGE" + "HOUSEHOLDS"."INCOME_RANK") as agg_1 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE ("HOUSEHOLDS"."INCOME_RANK" <= 0.5175600047902704 OR "HOUSEHOLDS"."INCOME_RANK" BETWEEN 0.11039843243152206 AND 0.9340555214959725) AND "HOUSEHOLDS"."AGE_REF" BETWEEN 53 AND 66 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" <= 1.0 AND "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" >= 4782342 AND "HOUSEHOLDS"."INCOME_RANK_5" BETWEEN 0.11319381186955589 AND 0.15733239486514625 AND "EXPENDITURES"."MONTH" >= 5