SELECT MAX("EXPENDITURES"."EXPENDITURE_ID" + "EXPENDITURES"."MONTH") as agg_0 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."GIFT" BETWEEN 0 AND 1 AND ("EXPENDITURES"."IS_TRAINING" != 1 OR ("EXPENDITURES"."IS_TRAINING" BETWEEN 1 AND 2 AND "EXPENDITURES"."IS_TRAINING" != 1)) AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" >= 1.0