SELECT AVG("HOUSEHOLD_MEMBERS"."YEAR") as agg_0, COUNT(*) as agg_1, AVG("HOUSEHOLDS"."INCOME_RANK_5") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."PRODUCT_CODE" BETWEEN 304383 AND 375234 AND "EXPENDITURES"."HOUSEHOLD_ID" <= 4953047 AND "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" BETWEEN 3182844 AND 5428002