SELECT COUNT(*) as agg_0, COUNT(*) as agg_1 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK" <= 0.140139379664817 AND "HOUSEHOLD_MEMBERS"."AGE" != 35 AND "EXPENDITURES"."YEAR" >= 2015 AND "EXPENDITURES"."IS_TRAINING" BETWEEN 0 AND 1