SELECT AVG("EXPENDITURES"."HOUSEHOLD_ID" + "HOUSEHOLDS"."HOUSEHOLD_ID") as agg_0 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."HOUSEHOLD_ID" >= 3220407 AND "EXPENDITURES"."IS_TRAINING" != 1 AND "EXPENDITURES"."PRODUCT_CODE" <= 60573 AND "HOUSEHOLDS"."INCOME_RANK_MEAN" BETWEEN 0.34241681101899396 AND 0.9493380874376236 AND "HOUSEHOLDS"."AGE_REF" = 65