SELECT AVG("HOUSEHOLDS"."HOUSEHOLD_ID") as agg_0, MIN("HOUSEHOLDS"."INCOME_RANK_3" + "HOUSEHOLDS"."YEAR") as agg_1 FROM "HOUSEHOLDS"  WHERE "HOUSEHOLDS"."YEAR" BETWEEN 2015 AND 2018 AND "HOUSEHOLDS"."INCOME_RANK_5" >= 0.7038243521678882 AND ("HOUSEHOLDS"."INCOME_RANK_3" <= 0.4192236307814692 OR "HOUSEHOLDS"."INCOME_RANK_MEAN" >= 0.4624216818369368 OR "HOUSEHOLDS"."INCOME_RANK_3" <= 0.8493810763647417) AND "HOUSEHOLDS"."INCOME_RANK_1" BETWEEN 0.05319464043270545 AND 0.6007442843141981 AND "HOUSEHOLDS"."AGE_REF" BETWEEN 34 AND 59 AND "HOUSEHOLDS"."INCOME_RANK" <= 0.38791644131528474