SELECT SUM("EXPENDITURES"."COST" + "HOUSEHOLDS"."INCOME_RANK_5") as agg_0, MAX("EXPENDITURES"."PRODUCT_CODE") as agg_1 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."IS_TRAINING" BETWEEN 0 AND 1 AND ("HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" <= 3512071 OR "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" BETWEEN 3306148 AND 4058761)