SELECT MIN("HOUSEHOLDS"."YEAR") as agg_0, SUM("HOUSEHOLDS"."INCOME_RANK_3" + "EXPENDITURES"."PRODUCT_CODE") as agg_1 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_3" BETWEEN 0.872438620326721 AND 0.9142847852786478