SELECT COUNT(*) as agg_0, AVG("HOUSEHOLD_MEMBERS"."AGE" + "HOUSEHOLDS"."INCOME_RANK_5") as agg_1, SUM("HOUSEHOLDS"."INCOME_RANK_5" + "HOUSEHOLDS"."INCOME_RANK_MEAN") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_2" <= 0.9713694663041514 AND "HOUSEHOLD_MEMBERS"."MARITAL" <= 5 AND ("EXPENDITURES"."HOUSEHOLD_ID" >= 5152088 OR "EXPENDITURES"."YEAR" >= 2017 OR "EXPENDITURES"."HOUSEHOLD_ID" >= 3943774) AND "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" BETWEEN 5803597 AND 6504826