SELECT MIN("EXPENDITURES"."COST") as agg_0, SUM("HOUSEHOLD_MEMBERS"."WORK_STATUS" + "EXPENDITURES"."MONTH") as agg_1, COUNT(*) as agg_2 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_3" >= 0.07913745051628764 AND "HOUSEHOLDS"."YEAR" != 2019 AND "EXPENDITURES"."MONTH" >= 8 AND "HOUSEHOLDS"."INCOME_RANK_5" >= 0.8779789430727456 AND "EXPENDITURES"."IS_TRAINING" = 1 AND "EXPENDITURES"."PRODUCT_CODE" >= 506720