SELECT MIN("EXPENDITURES"."EXPENDITURE_ID" + "EXPENDITURES"."GIFT") as agg_0, MIN("HOUSEHOLDS"."INCOME_RANK_1" + "HOUSEHOLD_MEMBERS"."YEAR") as agg_1, SUM("HOUSEHOLD_MEMBERS"."WORK_STATUS" + "HOUSEHOLDS"."INCOME_RANK_5") as agg_2 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."YEAR" BETWEEN 2015 AND 2017 AND "HOUSEHOLD_MEMBERS"."AGE" >= 31