SELECT AVG("HOUSEHOLDS"."YEAR") as agg_0, SUM("HOUSEHOLDS"."YEAR" + "HOUSEHOLDS"."INCOME_RANK_5") as agg_1, MIN("HOUSEHOLDS"."INCOME_RANK_MEAN" + "HOUSEHOLDS"."INCOME_RANK") as agg_2 FROM "HOUSEHOLDS"  WHERE "HOUSEHOLDS"."INCOME_RANK" <= 0.2257069324226538 AND "HOUSEHOLDS"."INCOME_RANK_5" BETWEEN 0.7802234630129198 AND 0.8290163216612175