SELECT AVG("HOUSEHOLDS"."HOUSEHOLD_ID" + "HOUSEHOLDS"."INCOME_RANK_MEAN") as agg_0, AVG("HOUSEHOLD_MEMBERS"."SEX") as agg_1 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."YEAR" BETWEEN 2016 AND 2017 AND "HOUSEHOLDS"."INCOME_RANK_4" >= 0.6563545275357638 AND "EXPENDITURES"."MONTH" >= 5 AND ("EXPENDITURES"."IS_TRAINING" = 1 OR "EXPENDITURES"."IS_TRAINING" >= 0 OR "EXPENDITURES"."IS_TRAINING" != 1) AND "EXPENDITURES"."PRODUCT_CODE" <= 65919 AND "HOUSEHOLDS"."HOUSEHOLD_ID" BETWEEN 4486379 AND 5449338