SELECT MAX("EXPENDITURES"."EXPENDITURE_ID" + "HOUSEHOLD_MEMBERS"."MARITAL") as agg_0 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."AGE" != 5 AND "HOUSEHOLD_MEMBERS"."WORK_STATUS" IS NOT NULL AND "HOUSEHOLDS"."INCOME_RANK_5" >= 0.015127261816733196 AND "EXPENDITURES"."COST" <= 4.830331710043234 AND ("EXPENDITURES"."MONTH" <= 4 OR "EXPENDITURES"."MONTH" = 6 OR "EXPENDITURES"."MONTH" != 8)