SELECT SUM("HOUSEHOLDS"."INCOME_RANK_3" + "EXPENDITURES"."COST") as agg_0, SUM("HOUSEHOLDS"."INCOME_RANK_2") as agg_1, AVG("HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" + "HOUSEHOLDS"."INCOME_RANK_5") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "EXPENDITURES"."HOUSEHOLD_ID" <= 3916049 AND "HOUSEHOLD_MEMBERS"."AGE" != 7