SELECT MAX("EXPENDITURES"."GIFT" + "HOUSEHOLDS"."YEAR") as agg_0 FROM "HOUSEHOLDS" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLD_MEMBERS"."WORK_STATUS" <= 1.0 AND "HOUSEHOLD_MEMBERS"."AGE" != 66 AND "HOUSEHOLDS"."INCOME_RANK_5" <= 0.44561089813773397 AND ("HOUSEHOLDS"."INCOME_RANK_1" >= 0.4015078115210395 OR "HOUSEHOLDS"."INCOME_RANK_3" >= 0.08832684776063743)