SELECT COUNT(*) as agg_0, MIN("HOUSEHOLDS"."INCOME_RANK_4") as agg_1, MIN("EXPENDITURES"."YEAR") as agg_2 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_3" BETWEEN 0.07758648375970026 AND 0.7880026433582161 AND "EXPENDITURES"."GIFT" >= 0 AND "EXPENDITURES"."EXPENDITURE_ID" >= 609332 AND "EXPENDITURES"."YEAR" BETWEEN 2017 AND 2018 AND "HOUSEHOLDS"."INCOME_RANK_MEAN" BETWEEN 0.33233299940660754 AND 0.9677653297339633