SELECT MAX("EXPENDITURES"."MONTH" + "HOUSEHOLDS"."INCOME_RANK") as agg_0, AVG("HOUSEHOLDS"."INCOME_RANK") as agg_1 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE ("HOUSEHOLD_MEMBERS"."MARITAL" != 1 OR "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" BETWEEN 4016908 AND 4097709 OR "HOUSEHOLD_MEMBERS"."MARITAL" >= 1) AND ("HOUSEHOLDS"."AGE_REF" = 27 OR "HOUSEHOLDS"."AGE_REF" <= 60 OR "HOUSEHOLDS"."AGE_REF" BETWEEN 39 AND 64) AND "EXPENDITURES"."GIFT" >= 0 AND "HOUSEHOLD_MEMBERS"."YEAR" = 2016