SELECT AVG("HOUSEHOLD_MEMBERS"."AGE" + "HOUSEHOLDS"."INCOME_RANK_MEAN") as agg_0, COUNT(*) as agg_1, MAX("EXPENDITURES"."COST") as agg_2 FROM "EXPENDITURES" LEFT OUTER JOIN "HOUSEHOLDS" ON "EXPENDITURES"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "HOUSEHOLD_MEMBERS" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_3" <= 0.3228012307804032 AND "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" >= 3681657 AND ("HOUSEHOLD_MEMBERS"."MARITAL" >= 1 OR ("HOUSEHOLD_MEMBERS"."MARITAL" <= 5 AND "HOUSEHOLD_MEMBERS"."MARITAL" <= 1))