SELECT MAX("HOUSEHOLDS"."INCOME_RANK_MEAN") as agg_0, SUM("EXPENDITURES"."YEAR" + "HOUSEHOLDS"."INCOME_RANK_MEAN") as agg_1, AVG("EXPENDITURES"."EXPENDITURE_ID") as agg_2 FROM "HOUSEHOLD_MEMBERS" LEFT OUTER JOIN "HOUSEHOLDS" ON "HOUSEHOLD_MEMBERS"."HOUSEHOLD_ID" = "HOUSEHOLDS"."HOUSEHOLD_ID" LEFT OUTER JOIN "EXPENDITURES" ON "HOUSEHOLDS"."HOUSEHOLD_ID" = "EXPENDITURES"."HOUSEHOLD_ID"  WHERE "HOUSEHOLDS"."INCOME_RANK_4" >= 0.28706607431725933